const items = $input.all();

const updatedItems = items.map((item) => {
  let query = item?.json?.output;

  if (!query) return item;

  // Step 1: Remove triple backticks + optional 'sql'
  query = query.replace(/^```sql\\n|^```sql\n|^```sql\r\n/, "")
               .replace(/\\n```$|\n```$|\r\n```$/, "");

  // Step 2: Unescape \n â†’ real newlines
  query = query.replace(/\\n/g, "\n").trim();

  // Step 3: Convert table name to TitleCase and wrap in quotes
  const upperQuery = query.toUpperCase();
  if (upperQuery.startsWith("SELECT") && query.trim().endsWith(";")) {
    const fromIndex = upperQuery.indexOf("FROM");
    if (fromIndex !== -1) {
      const tableNameStart = fromIndex + 5;
      const tableNameEnd = query.indexOf(" ", tableNameStart);
      const tableName = query.slice(
        tableNameStart,
        tableNameEnd !== -1 ? tableNameEnd : query.length - 1
      ).trim();

      // const titleCaseTableName = `"${tableName.charAt(0).toUpperCase() + tableName.slice(1).toLowerCase()}"`;

      //query = query.replace(tableName, titleCaseTableName);
    }
  }

  // Step 4: Clean up spacing
  item.json.output = query.replace(/\s+/g, " ").trim();
  return item;
});

return updatedItems;

const items = $input.all();

// Flatten each row to a readable format
const rows = items.map((item, index) => {
  const row = item.json;
  const readable = Object.entries(row)
    .map(([key, value]) => `**${key}**: ${value}`)
    .join(', ');
  return `Row ${index + 1}: ${readable}`;
});

return [
  {
    json: {
      summaryInput: rows.join('\n')
    }
  }
];
